import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [key, setKey] = useState('');
  const [encryptedMessage, setEncryptedMessage] = useState('');
  const [decryptedMessage, setDecryptedMessage] = useState('');

  // Function to handle encryption
  const handleEncrypt = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/encrypt', {
        message: message,
        key: key
      });
      setEncryptedMessage(response.data.encrypted_message);
    } catch (error) {
      console.error('Error during encryption:', error);
    }
  };

  // Function to handle decryption
  const handleDecrypt = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/decrypt', {
        encrypted_message: encryptedMessage,
        key: key,
        vi: vi
      });
      setDecryptedMessage(response.data.decrypted_message);
    } catch (error) {
      console.error('Error during decryption:', error);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Encryption and Decryption AES-256</h1>

      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Enter message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
      </div>

      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Enter encryption key"
          value={key}
          onChange={(e) => setKey(e.target.value)}
        />
      </div>

      <button onClick={handleEncrypt}>Encrypt</button>

      {encryptedMessage && (
        <div>
          <h3>Encrypted Message:</h3>
          <p>{encryptedMessage}</p>
        </div>
      )}

      {encryptedMessage && (
        <>
          <button onClick={handleDecrypt}>Decrypt</button>

          {decryptedMessage && (
            <div>
              <h3>Decrypted Message:</h3>
              <p>{decryptedMessage}</p>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
